
define(['dojo/topic', 'dojo/ready', "hotplate/hotDojoAppContainer/AppContainer"  ], function( topic, ready, AppContainer ){

  topic.subscribe( 'hotplateModulesLoaded', function(){

    // Create the "application" object, places them in the right spot, start it up
    appContainer = new AppContainer( {} , 'appContainer');
    appContainer.startup();
  }); 

});

